mapper.select:
Select count(1) From role Where 0=0
and name like '%${name}%'
and creator=#{creator}

mapper.update:
	   update role set name = #{name},
	                    status = #{status},
	                    updateTime = #{updateTime},
	                    type=#{type}
        where  id=#{id}

mapper.delete:
	    delete from role 
	    where  id in 
	       
           #{id}  
         ;
        delete from role_login 
        where roleId in
            
           #{id}  
         ;
        
        delete from role_power where roleId in
           
           #{id}  
         ;

mapper.select:
Select * From role a join role_user b on a.id=b.roleId 
Where 0=0  

and a.roleType=#{roleType}   


and b.userId=#{userId}   

limit 0,1
