mapper.select:
select * From role Where 0=0
and name like '%${name}%'
limit #{pageBean.startRowNum},#{pageBean.rows}

mapper.update:
	   update role set name = #{name},
	                    status = #{status},
	                    updateTime = #{updateTime}
        where  id=#{id}

mapper.delete:
	    delete from role 
	    where  id in 
	       
           #{id}  
         ;
        delete from role_login 
        where roleId in
            
           #{id}  
         ;
        
        delete from role_power where roleId in
           
           #{id}  
         ;
