choose.otherwise:
select a.id,a.text,(case when exists(select 1 from role_user r where r.roleId=#{roleId} and r.userId=a.id) then true else false end) checked From view_user a Where isDeleted=0
and a.flag like '${flag}%'
order by checked desc;

mapper.select:
select * From user_info Where isDeleted=0
and text like '%${text}%'
limit #{pageBean.startRowNum},#{pageBean.rows}

mapper.update:
	   update user_info set text = #{text},
	                    password = #{password},
	                    isActives = #{isActives}
        where  id=#{id}

mapper.select:
Select * From user_info  Where isDeleted=0  

	And  id=#{id}


	And  text=#{text}

limit 0,1

mapper.delete:
	    update   user_info set isDeleted=1   Where 1 = 1
	    And id in 
	       
           #{id}  
         ;
