mapper.select:
select a.*  From  file_management a 
Where status ='正常'

	and a.parentId=#{parentId}


	and a.type=#{type}

select a.*,name text,case when  exists(select 1 from file_management t where t.parentId=a.id and t.type='文件夹') then 'closed' else 'open' end state  From  file_management a 
Where 0=0

	and a.parentId=#{parentId}


	and a.type=#{type}

order by seq

select a.*,(select name from view_user where id=a.creator) creatorName,(select name from view_user where id=a.modifier) modifyName  From  file_management a 
Where status ='正常'

	and a.parentId=#{parentId}


	and a.type=#{type}

order by seq 
limit  #{pageBean.startRowNum},#{pageBean.rows}

 Select count(1) From  file_management a
 Where status ='正常'
 
	and a.parentId=#{parentId}


	and a.type=#{type}

mapper.update:
	   update file_management set   name = #{name},
	                    remark = #{remark},
	                    modifier = #{modifier},
	                    modifydate = #{modifydate}
        where  id=#{id} ;
        update  file_management set relativeUrl=replace (relativeUrl,#{historyRelativeUrl},#{nowUrl}) 
       where relativeUrl like '${historyRelativeUrl}%';

mapper.select:
Select a.*  From file_management a  Where 0=0  

	and a.name=#{name}


	and a.parentId=#{parentId}

limit 0,1

mapper.delete:
	    update  file_management  set status='删除',deleter=#{deleter},modifydate=#{modifydate}   Where 1 = 1
	    And id in 
	       
           #{id}  
         ;

mapper.select:
select a.*,(select name from view_user where id=a.creator) creatorName,(select name from file_management where id=a.parentId) location  From  file_management a 
Where status ='删除'

	and a.parentId=#{parentId}


	and a.type=#{type}

order by seq 
limit  #{pageBean.startRowNum},#{pageBean.rows}

select a.*,(select name from view_user where id=a.creator) creatorName,(select name from file_management where id=a.parentId) location  From  file_management a 
Where status ='删除'

	and a.parentId=#{parentId}


	and a.type=#{type}

 Select count(1) From  file_management a
 Where status ='删除'
 
	and a.parentId=#{parentId}


	and a.type=#{type}

mapper.delete:
	    delete from   file_management    Where 1 = 1
	    And id in 
	       
           #{id}  
         ;

mapper.select:
	    select * from   file_management    Where 1 = 1
	    And id in 
	       
           #{id}  
         ;

mapper.update:
	    update  file_management set status='正常'    Where 1 = 1
	    And id in 
	       
           #{id}  
         ;

mapper.select:
  select *,(select GROUP_CONCAT(b.type) from folder_shared a  join shared_role b on a.id=b.sharedId where a.fileId=file_management.id)as oprType
  ,(select name from view_user where id=file_management.creator) creatorName,(select userName from login_info where id=file_management.modifier) modifyName from file_management 
  	
  	
  	
  	where id in(
	select DISTINCT t1.fileId from folder_shared t1 
	join shared_role t2 on t1.id=t2.sharedId
	join role t3 on t2.roleId=t3.id
	join role_login t4 on t3.id=t4.roleId
	where t4.loginId=#{userId}
	) 
	 
	
		where 1=1
	
	
	and status='正常'
   
   	and parentId=#{parentId}
   
   
   	and type=#{type}
   
   order by seq 
   limit  #{pageBean.startRowNum},#{pageBean.rows}

   select count(1) from file_management
   	
  	
  	
  	where id in(
	select DISTINCT t1.fileId from folder_shared t1 
	join shared_role t2 on t1.id=t2.sharedId
	join role t3 on t2.roleId=t3.id
	join role_login t4 on t3.id=t4.roleId
	where t4.loginId=#{userId}
	) 
	 
	
		where 1=1
	
	
	and status='正常'
   
   	and parentId=#{parentId}
   
   
   	and type=#{type}

select DISTINCT t5.id,concat(t6.name,'-',t5.name) name from folder_shared t1 join shared_role t2 on t1.id=t2.sharedId and t1.status='指定角色可见' join role t3 on t2.roleId=t3.id join role_login t4 on t3.id=t4.roleId join file_management t5 on t1.fileId =t5.id join top_folder t6 on t5.topFileId=t6.id and t6.type='资源文件夹' where t4.loginId=#{userId}
and t1.topFileId!=#{id}
union select DISTINCT t5.id,t5.name name from folder_shared t1 join shared_role t2 on t1.id=t2.sharedId and t1.status='指定角色可见' join role t3 on t2.roleId=t3.id join role_login t4 on t3.id=t4.roleId join top_folder t5 on t1.fileId =t5.id and t5.type='资源文件夹' where t4.loginId=#{userId}
and t1.topFileId!=#{id}
union
select DISTINCT t5.id,concat(t6.name,'-',t5.name) name from folder_shared t1 join shared_role t2 on t1.id=t2.sharedId and t1.status='所有人可见' join file_management t5 on t1.fileId =t5.id join top_folder t6 on t5.topFileId=t6.id and t6.type='资源文件夹'
and t1.topFileId!=#{id}
union select DISTINCT t5.id,t5.name name from folder_shared t1 join shared_role t2 on t1.id=t2.sharedId and t1.status='所有人可见' join top_folder t5 on t1.fileId =t5.id and t5.type='资源文件夹'
and t1.topFileId!=#{id}

	select DISTINCT t5.id,concat(t6.name,'-',t5.name) name from folder_shared t1 
	join shared_role t2 on t1.id=t2.sharedId and t1.status='指定角色可见'
	join role t3 on t2.roleId=t3.id
	join role_login t4 on t3.id=t4.roleId
	join file_management t5 on t1.fileId =t5.id
	join top_folder t6 on t5.topFileId=t6.id and t6.type='评估文档'
	join assess_theme t7 on t6.id=t7.topFileId
	where t4.loginId=#{userId}
	
		and t1.topFileId!=#{id}
	
	
   	 and t7.beginDate <=#{date} and t7.endDate >= #{date}
   
   
   		and t6.id not in 
	   	
	   		#{vo.id}
	   	
   
	union
	select DISTINCT t5.id,t5.name name from folder_shared t1 
	join shared_role t2 on t1.id=t2.sharedId and t1.status='指定角色可见'
	join role t3 on t2.roleId=t3.id
	join role_login t4 on t3.id=t4.roleId
	join top_folder t5 on t1.fileId =t5.id  and t5.type='评估文档'
	join assess_theme t7 on t5.id=t7.topFileId
	where t4.loginId=#{userId}
	
		and t1.topFileId!=#{id}
	
	
   	 and t7.beginDate <=#{date} and t7.endDate >= #{date}
   
   
   		and t5.id not in 
	   	
	   		#{vo.id}
	   	
   
   
	union
	
	select DISTINCT t5.id,concat(t6.name,'-',t5.name) name from folder_shared t1 
	join shared_role t2 on t1.id=t2.sharedId and t1.status='所有人可见'
	join file_management t5 on t1.fileId =t5.id
	join top_folder t6 on t5.topFileId=t6.id and t6.type='评估文档'
	join assess_theme t7 on t6.id=t7.topFileId
	where 1=1
	
		and t1.topFileId!=#{id}
	
	
   	 and t7.beginDate <=#{date} and t7.endDate >= #{date}
   
   
   		and t6.id not in 
	   	
	   		#{vo.id}
	   	
   
   union
	select DISTINCT t5.id,t5.name name from folder_shared t1 
	join shared_role t2 on t1.id=t2.sharedId and t1.status='所有人可见'
	join top_folder t5 on t1.fileId =t5.id  and t5.type='评估文档'
	join assess_theme t7 on t5.id=t7.topFileId
	where 1=1
	
		and t1.topFileId!=#{id}
	
	
   	 and t7.beginDate <=#{date} and t7.endDate >= #{date}
   
   
   		and t5.id not in 
	   	
	   		#{vo.id}
