mapper.select:
select a.*,(select name from view_user where id=a.creator) creatorName,b.isSubmit isSubmit
From file_submit a join submit_teacher b on a.id=b.submitId join teacher_info c on b.teacherId=c.id Where 0=0
and a.title like '%${title}%'
and c.userId=#{userId}
order by b.isSubmit,createdate desc limit #{pageBean.startRowNum},#{pageBean.rows}

Select count(1) From file_submit a join submit_teacher b on a.id=b.submitId join teacher_info c on b.teacherId=c.id Where 0=0
and a.title like '%${title}%'
and c.userId=#{userId}

select a.*,(select name from view_user where id=a.creator) creatorName, (select count(1) from submit_teacher where submitId=a.id) totalNumber, (select count(1) from submit_teacher where submitId=a.id and isSubmit='已提交') submitNumber From file_submit a Where 0=0
and a.title like '%${title}%'
and a.id in (select submitId from submit_teacher t1 join teacher_info t2 on t1.teacherId=t2.id where t2.userId=#{userId} )
and a.creator=#{creator}
order by createdate desc limit #{pageBean.startRowNum},#{pageBean.rows}

mapper.update:
	   update file_submit set  title = #{title},
	                    content = #{content},
	                    accessoryUrl = #{accessoryUrl},
	                    endDate=#{endDate},
	                    fileId=#{fileId}
        where  id=#{id}

mapper.select:
Select a.*,(select name from view_user where id=a.creator) creatorName,b.submitUrl submitUrl,c.name teacherName  
From file_submit a join submit_teacher b on a.id=b.submitId  join teacher_info c on b.teacherId=c.id
Where 0=0  

	and c.userId=#{userId}


	and a.id=#{submitId}

mapper.delete:
	    delete from   file_submit    Where 1 = 1
	    And id in 
	       
           #{id}  
