mapper.select:
   select a.*  From  material a   Where 0=0
   
		and a.superviseId=#{superviseId}
	
	
		and a.unitId=#{unitId}
	
	
		and a.projectId=#{projectId}
	
	
		and a.type=#{type}

select max(name) from material a   Where a.name like '${name}%'

	and a.superviseId=#{superviseId}


	and a.unitId=#{unitId}


	and a.projectId=#{projectId}

select count(1) total,sum(case when status='通过' then 1 else 0 end) passNum,sum(case when status='不通过' then 1 else 0 end) unpassNum from material a where 1=1 

	and a.id=#{id}


	and a.superviseId=#{superviseId}


	and a.unitId=#{unitId}


	and a.projectId=#{projectId}
