mapper.select:
select *,(select name from project t1 where t1.id=a.pid) parentName From project a Where status='启用'
and a.code like '${code}%'
and a.type =#{type}
and a.name like '%${name}%'
order by a.seq asc limit #{pageBean.startRowNum},#{pageBean.rows}

mapper.delete:
	    update   project set status='删除'   Where 1 = 1
	    And id in 
	       
           #{id}  
         ;
