mapper.select:
select a.* From  supervise a 
Where 0=0
  
	and a.type=#{type}


	and  a.name like '%${name}%'  


	 and a.superviseDate>=#{startDate}

 
	 and a.superviseDate<=#{endDate}

limit  #{pageBean.startRowNum},#{pageBean.rows}

 Select count(1) From  supervise a 
Where 0=0

	and a.type=#{type}


	and  a.name like '%${name}%'  


	 and a.superviseDate>=#{startDate}

 
	 and a.superviseDate<=#{endDate}

mapper.delete:
	    delete from   supervise   Where 1 = 1
	    And id in 
	       
           #{id}  
         ;
        delete from supervise_project where 1=1
        and superviseId in
           
           #{id}  
         ;
        delete from supervise_unit where 1=1
        and superviseId in
           
           #{id}  
         ;

mapper.select:
	select a.*,c.name schoolName,b.flowStatus,b.unitId unitId,b.step step 
	from supervise a 
	join supervise_unit b on a.id=b.superviseId and a.status='启用' 
	join unit_info c on c.id=b.unitId
	
		join inspector_unit d on d.unitId=c.id 
		join inspector e on e.id=d.inspectorId and e.userId=#{userId}
	
	where 1=1
	
   	and  c.flag  like '${flag}%'  
   
   
   	and  c.typeId=#{typeId}
   
   
   	and  c.categoryId=#{categoryId}
   
   
   	and  a.id=#{schoolId}
   
   
   	and a.id=#{superviseId}
   
   order by a.superviseDate desc
   limit  #{pageBean.startRowNum},#{pageBean.rows}

	select count(1) 
	from supervise a 
	join supervise_unit b on a.id=b.superviseId  and a.status='启用' 
	join unit_info c on c.id=b.unitId
	
		join inspector_unit d on d.unitId=c.id 
		join inspector e on e.id=d.inspectorId and e.userId=#{userId}
	
	where 1=1
	
   	and  c.flag  like '${flag}%'  
   
   
   	and  c.typeId=#{typeId}
   
   
   	and  c.categoryId=#{categoryId}
   
   
   	and  a.id=#{schoolId}
   
   
	   	and a.id=#{superviseId}

select t1.*,t3.name unitName,t4.id teacherId,t4.name teacherName,t4.wechat wechat,t4.userId userId from supervise t1 join supervise_unit t2 on t1.id=t2.superviseId
and t1.id=#{superviseId}
join unit_info t3 on t2.unitId=t3.id and t3.type='2' join teacher_info t4 on t3.id=t4.schoolId and t4.isDeleted='0' join user_info t5 on t4.userId=t5.id and t5.isActives='1' and t5.isDeleted='0' join role_user t6 on t6.userId=t5.id join role t7 on t6.roleId=t7.id
and roleType=#{roleType}
